insert  into `cms_article`(`id`,`title`,`content`,`picture`,`channel_id`,`category_id`,`user_id`,`hits`,`hot`,`status`,`deleted`,`created`,`updated`,`commentCnt`,`articleType`,`complainCnt`) values (89,'测试标签','标签','',3,11,46,20,1,1,0,'2019-10-23 11:03:26','2019-10-23 11:03:26',0,0000000000,0),(92,'测试标签的的重复','测试标签的的重复测试标签的的重复测试标签的的重复测试标签的的重复测试标签的的重复','',4,14,46,20,0,1,0,'2019-10-23 11:11:24','2019-10-23 11:11:24',0,0000000000,0),(96,'测试图片集合','','20191024/dc65bb4d-f044-4bb8-8f4b-b33fb9c4add0.jpg',1,2,46,20,1,1,0,'2019-10-24 15:55:03','2019-10-24 15:55:03',0,0000000000,0),(97,'测试图片','[]','20191024/80efd077-9861-4472-b16c-591421b9c53e.jpeg',1,2,46,20,1,1,0,'2019-10-24 16:06:20','2019-10-24 16:06:20',0,0000000000,0),(98,'测试图片的集合','[{\"desc\":\"篮球\",\"picUrl\":\"20191024/4ad74add-6ea5-4cfc-b209-9d6db49fe0ef.jpeg\"},{\"desc\":\"红花\",\"picUrl\":\"20191024/ac4781fc-9c10-4d96-9f6b-25703775967d.jpg\"}]','20191024/c43410c9-a047-49f2-a5e4-56071b788034.jpeg',1,2,46,21,1,1,0,'2019-10-24 16:09:07','2019-10-24 16:19:52',0,0000000001,0),(99,'最终版测试的轮播图篇','[{\"desc\":\"红色的花花\",\"picUrl\":\"20191024/cefcb868-6c26-49ea-a710-9ab2ea79abec.jpg\"},{\"desc\":\"水啊\",\"picUrl\":\"20191024/e3992a7e-abbe-4bb1-9a97-359b64348b0e.jpg\"},{\"desc\":\"小手拿着花\",\"picUrl\":\"20191024/f3e383b9-cf2c-407f-ad2f-424200e9a141.jpg\"}]','20191024/250df96a-b16c-455f-9093-12f73e52e892.jpg',1,2,46,21,1,1,0,'2019-10-24 18:08:54','2019-10-24 18:09:25',0,0000000001,3),(100,'测试标签','测试标签测试标签测试标签测试标签测试标签测试标签测试标签测试标签','20191026/fb6206d6-ac9b-446a-887b-c085d058fd18.jpeg',6,22,47,20,1,1,0,'2019-10-26 08:36:18','2019-10-26 08:41:28',0,0000000000,1),(101,'tttttt','ddddd','/20191027/3e068b73-8666-4b9f-abab-957cc8883a0b.jpg',3,10,46,20,1,1,0,'2019-10-27 13:05:28','2019-10-27 13:05:28',0,0000000000,0),(102,'第三方对方','测试内容','20191216/c7603d78-5436-4683-b8fe-8e1bae2aebbe.jpg',3,10,3,20,1,1,0,'2019-10-28 10:22:32','2019-10-28 10:22:32',0,0000000000,0),(103,'都是范德萨','测试内容11',NULL,NULL,NULL,NULL,20,0,1,0,'2019-10-28 10:24:36','2019-10-28 10:24:36',0,0000000000,0),(104,'双方都','测试内容11',NULL,NULL,NULL,NULL,20,0,1,0,'2019-10-28 10:28:58','2019-10-28 10:28:58',0,0000000000,0),(106,'帝国主义','社会的德。要将国家价值目标、社会价值准则和公民价值规范，有机融入公民道德建设各方面、全过程，更好发挥出引领作用。\r\n\r\n推进新时代公民道德建设，必须聚力培养担当民族复兴大任的时代新人。担当民族复兴大任的时代新人，必须是在思想水平、政治觉悟、道德品质、文化素养、精神状态等方面同新时代要求相符合的，这是公民道德建设的出发点和落脚点，一切工作都要聚焦这个目标，须臾不能偏离。要不断深化对时代新人丰富内涵、时代特质、根本要求的认识和理解，更好担负起培养时代新人的重要职责。\r\n\r\n千里之行，始于足下。公民道德建设重在抓小抓细抓实。各地各部门要适应新时代新要求，坚持目标导向和问题导向相统一，进一步加大工作力度，把握规律、积极创新，持之以恒、久久为功，推动全民道德素质和社会文明程度达到一个新高度。\r\n',NULL,4,20,NULL,20,0,1,0,'2019-10-29 10:04:06','2019-10-29 10:04:06',0,0000000000,0),(107,'社会主义','人民日报评论员：推动全民道德素质和社会文明程度达到新高度\r\n\r\n2019年10月28日07:29    来源：人民网－人民日报\r\n\r\n《新时代公民道德建设实施纲要》的公布是我国社会主义精神文明建设的一件大事。中国特色社会主义进入新时代，对公民道德建设提出了新的更高要求。加强公民道德建设、提高全社会道德水平，是全面建成小康社会、全面建设社会主义现代化强国的战略任务，是适应社会主要矛盾变化、满足人民对美好生活向往的迫切需要，是促进社会全面进步、人的全面发展的必然要求。《纲要》的出台对于推动全民道德素质和社会文明程度达到一个新高度，决胜全面建成小康社会，开启全面建设社会主义现代化国家新征程，具有十分重要的意义。\r\n\r\n党的十八大以来，以习近平同志为核心的党中央高度重视公民道德建设，\r\n',NULL,4,20,NULL,20,0,1,0,'2019-10-29 10:04:06','2019-10-29 10:04:06',0,0000000000,0),(108,'资本主义','作出一系列重要部署，推动思想道德建设取得显著成效。讲道德、尊道德、守道德，追求高尚的道德蔚为风尚；崇尚英雄、尊重模范、学习先进的氛围更加浓厚，道德领域呈现积极健康向上的良好态势。在今年庆祝新中国成立70周年系列活动中，全国各族人民表现出的爱国情感朴素而炽热，全社会团结一致、奋发昂扬，民族自信心和自豪感进一步增强，展示了我国公民道德建设所取得的丰硕成果。当代中国积累了丰厚的道德资源，这是我们在新时代进一步做好公民道德建设的重要基础。\r\n\r\n推进新时代公民道德建设，必须以习近平新时代中国特色社会主义思想为指导。在全民族牢固树立中国特色社会主义共同理想，在全社会大力弘扬社会主义核心价值观，使公民个人的道德追求融入实现中国梦的伟大征程，使爱国主义、集体主义、社会主义思想广为弘扬，使新时代的公民道德建设凝聚起实现中华民族伟大复兴中国梦的强大精神动力。\r\n\r\n推进新时代公民道德建设，必须坚持以社会主义核心价值观为引领。社会主义核心价值观是当代中国在价值观念上的最大公约数，其实就是一种德，既是个人的德，也是国家的德、社会的德。要将国家价值目标、社会价值准则和公民价值规范，有机融入公民道德建设各方面、全过程，更好发挥出引领作用。\r\n\r\n推进新时代公民道德建设，必须聚力培养担当民族复兴大任的时代新人。担当民族复兴大任的时代新人，必须是在思想水平、政治觉悟、道德品质、文化素养、精神状态等方面同新时代要求相符合的，这是公民道德建设的出发点和落脚点，一切工作都要聚焦这个目标，须臾不能偏离。要不断深化对时代新人丰富内涵、时代特质、根本要求的认识和理解，更好担负起培养时代新人的重要职责。\r\n\r\n千里之行，始于足下。公民道德建设重在抓小抓细抓实。各地各部门要适应新时代新要求，坚持目标导向和问题导向相统一，进一步加大工作力度，把握规律、积极创新，持之以恒、久久为功，推动全民道德素质和社会文明程度达到一个新高度。\r\n',NULL,4,20,NULL,20,0,1,0,'2019-10-29 10:04:06','2019-10-29 10:04:06',0,0000000000,0),(113,'测试文章','<pre>cms_article_tag_middle1</pre>\n<pre><img src=\"/pic/image/20191101/20191101102948_980.jpg\" alt=\"\" /></pre>\n<pre>cms_article_tag_middle1</pre>\n<pre>cms_article_tag_middle1</pre>\n<pre>cms_article_tag_middle1</pre>\n<pre>cms_article_tag_middle1</pre>','20191101/88dbc45d-5784-424b-89dc-419f782d73bb.jpeg',1,2,48,20,0,1,0,'2019-11-01 10:30:27','2019-11-01 10:30:27',0,0000000000,0),(114,'测试文章','<pre>cms_article_tag_middle1</pre>\n<pre><img src=\"/pic/image/20191101/20191101102948_980.jpg\" alt=\"\" /></pre>\n<pre>cms_article_tag_middle1</pre>\n<pre>cms_article_tag_middle1</pre>\n<pre>cms_article_tag_middle1</pre>\n<pre>cms_article_tag_middle1</pre>','20191101/98da4730-ead1-4061-8845-7896952f8dc1.jpeg',1,2,48,20,0,1,0,'2019-11-01 10:32:31','2019-11-01 10:32:31',0,0000000000,0),(115,'实打实大苏打','<img src=\"/pic/image/20191101/20191101104037_677.jpeg\" alt=\"\" />','20191101/adf360da-6280-41bd-99af-b300ade0d7af.jpg',3,11,48,20,1,2,0,'2019-11-01 10:40:53','2019-11-01 10:40:53',0,0000000000,0),(116,'我要测试的文章一二三四五六七八九十式一','测试发布文章<img src=\"/pic/image/20191101/20191101104603_37.jpg\" alt=\"\" />','20191101/36bcab59-6c75-4033-96b1-6fb5d568c67f.jpg',2,6,48,20,1,1,0,'2019-11-01 10:46:43','2019-11-01 10:48:27',1,0000000000,0),(117,'文章图片','[{\"desc\":\"篮球\",\"picUrl\":\"20191101/1225d76a-e68f-46cf-b12e-f7e4b14c329d.jpeg\"},{\"desc\":\"小红花\",\"picUrl\":\"20191101/ddf4b23d-7858-4dea-b24c-1eced5dd43fc.jpg\"},{\"desc\":\"小白花\",\"picUrl\":\"20191101/eb029344-1c5c-4e5b-b8e4-9e7eb39a3549.jpg\"}]','20191101/4a7aeec1-c6b7-4471-ba97-b92f8ec02b74.jpeg',1,2,48,20,0,1,0,'2019-11-01 10:50:47','2019-11-01 10:51:06',0,0000000001,1),(118,' 测试文章','','20191120/92a2ddcf-3488-463b-85a7-0a9f910e1c50.jpg',7,26,57,20,0,0,1,'2019-11-20 10:46:49','2019-11-20 10:46:49',0,0000000000,0),(119,'投机炒作、违法发币、骗局不断 借区块链炒作行骗需警惕 ','','20191120/dbbca02f-91d1-4f01-987a-ea4e4995b04b.jpeg',2,4,57,20,1,1,0,'2019-11-20 10:49:49','2019-11-20 10:49:49',0,0000000000,0),(120,'assdsdds','','20191120/f4904edb-98fb-4700-b672-9895263c90f3.jpeg',3,10,57,20,0,0,1,'2019-11-20 10:59:54','2019-11-20 10:59:54',0,0000000000,0),(126,'五角大楼新报告：美军从叙利亚撤军为IS“回归”创造条件qq','<div class=\"a-info\" style=\"font-size:14px;color:#999999;font-family:&quot;background-color:#F9F9F9;\">\n	<span class=\"time\">2019-11-20 08:34</span>&nbsp;<span class=\"original\">温家越</span><span class=\"tag\"></span> \n</div>\n<div class=\"a-con\" style=\"color:#191919;font-size:16px;font-family:&quot;background-color:#F9F9F9;\">\n	<p>\n		【环球网报道 记者 温家越】据“今日俄罗斯”(RT)19日报道，美国五角大楼当天发布一份新报告显示，美军从叙利亚撤军为极端组织“伊斯兰国”(IS)的“回归”创造了条件，并认为美军应该留在叙利亚。\n	</p>\n	<p>\n		<img alt=\"RT报道截图\" src=\"/pic/image/20191120/20191120112201_255.jpg\" style=\"height:auto;\" /> \n	</p>\n	<p style=\"text-align:center;\">\n		RT报道截图\n	</p>\n	<p>\n		RT称，在19日发布的一份报告中，美国国防部监察长警告称，如果美国不在叙利亚东北部部署军力，IS将“可能有能力重建并开展更复杂的行动”。\n	</p>\n	<p>\n		RT提到，该报告援引国防情报局<span style=\"font-size:24px;\">的信息称，IS已“激活潜伏小组”以攻击库尔德武装，而后者还在抵抗土耳其的入侵，所以IS有“时<strong>间和空间</strong>”策划对西方的袭击。报告说</span>，IS “很可能……在建立秘密网络”，并试图释放关押在库尔德监狱的囚犯。\n	</p>\n	<p>\n		报道对此评论称，这份报告是对特朗普<strong>撤军计划的谴责，但IS卷土重来不仅取决于美国的存在，叙利亚政府及其俄罗斯盟友仍准备打击该恐怖组织，库尔德武装同样如此。RT认为，对特朗普</strong>撤军的恐慌似乎更像是军方官员的担忧，他们担心美国不能亲自监督这场战斗。\n	</p>\n	<p>\n		上月6日，白宫表示，美军方对土耳其即将进<span style=\"background-color:#E56600;\">入叙利亚北部展开军事行动“不支持、不参与”，美军会撤出相关区域。但同月23日，美国总统特朗普又称，“少量”美军将留在叙利亚，保护该地区的石油</span>设施。本月13日，美军车队穿越叙利亚至伊拉克边境，并在叙利亚东北部的几处油田停留。\n	</p>\n</div>','20191217/03da50fe-9162-45fd-8b57-645e02d82d8b.jpg',3,9,57,20,1,0,0,'2019-11-20 11:22:13','2019-11-20 16:07:19',0,0000000000,0),(127,'五角大楼新报告：美军从叙利亚撤军为IS“回归”创造条件1112233','<div class=\"a-info\" style=\"font-size:14px;color:#999999;font-family:&quot;background-color:#F9F9F9;\">\n	<span class=\"time\">2019-11-20 08:34</span>&nbsp;<span class=\"original\">温家越&nbsp; 2222555</span><span class=\"tag\"></span>\n</div>\n<div class=\"a-con\" style=\"color:#191919;font-size:16px;font-family:&quot;background-color:#F9F9F9;\">\n	<p>\n		【环球网报道 记者 温家越】据“今日俄罗斯”(RT)19日报道，美国五角大楼当天发布一份新报告显示，美军从叙利亚撤军为极端组织“伊斯兰国”(IS)的“回归”创造了条件，并认为美军应该留在叙利亚。\n	</p>\n	<